import React, { Component, Fragment } from 'react'

import { Card, Icon } from 'semantic-ui-react'

class GameTiles extends Component {
  state = {
    loading: false
  }

  render() {
    return (
      <Fragment>
        { this.props.games ? <Card.Group itemsPerRow={this.props.perRow}>
          <Icon name='angle double left' size='large' />
          {this.props.games ? this.props.games.map((game, i) => {
            let alt = `cover art for ${this.props.games[i]}`

            return (i === this.props.column && i <= this.props.tColumns) ? <Card style={{alignSelf: 'center'}}>
              <img src={this.props.images[i]} alt={alt} style={{width: '100%'}}></img>
              <p style={{color: 'black'}}>{game}</p>
            </Card> : null
          })
          :
            null
          }
          <Icon name='angle double right' />
        </Card.Group>
        :
          null
        }
      </Fragment>
    );
  }

}

export default GameTiles
