import React, { Component, Fragment } from 'react'

import { Card } from 'semantic-ui-react'

class GameTile extends Component {

  render() {
    return (
      <Fragment>
        { this.props.games ? <Card.Group itemsPerRow={this.props.perRow}>
          {this.props.games ? this.props.games.map((g, i) => {
            let alt = `cover art for ${this.props.games[i]}`
            return <Card style={{alignSelf: 'center'}}>
              <img src={this.props.images[i]} alt={alt} style={{width: '100%'}}></img>
            </Card>
          })
          :
            null
          }
        </Card.Group>
        :
          null
        }
      </Fragment>
    );
  }

}

export default GameTile
