import React, { Component, Fragment } from 'react';
import { Icon } from 'semantic-ui-react'

class Cart extends Component {

  render() {
    return (
      <Fragment>
        {
          this.props.cart.map((g, i) => {
            let alt = `cover art for ${g}`
            return <div key={i}>
              <p>
                <img alt={alt} src={this.props.cartImg[i]}
                  style={{height: '6em', width: '6em'}} />
                <br />
                <span>
                  {g}
                </span>
                <Icon name='trash alternate' size='small'
                  style={{opacity: 1}} onClick={this.props.remove} />
              </p>
            </div>
          })
        }
      </Fragment>
    );
  }

}

export default Cart;
