class GameTile extends Component {

  render() {
    let alt = `cover art for ${this.props.games[i]}`
      return (
        <Fragment>
          { his.props.games.map((g, i) => {
            return <div key={i}>
              <p>
                <img alt={alt} src={this.props.images[i]}
                  style={{height: '6em', width: '6em'}} />
                <br />
                <span>
                  {g}
                </span>
                <button onClick={this.props.addGame}>
                  Rent
                </button>
              </p>
              <br />
            </div>

          })
            }
            </Fragment>
    );
  }

}

export default GameTile;
